#!/bin/bash

# 检查 AAB 文件签名密钥的脚本

set -e

echo "🔍 检查 AAB 文件签名密钥..."
echo ""

if [ -z "$1" ]; then
    echo "使用方法: ./check-signing-key.sh <path-to-aab-file>"
    echo ""
    echo "示例:"
    echo "  ./check-signing-key.sh application-2807aafb-95b5-400f-aeba-cb036db858f1.aab"
    exit 1
fi

AAB_FILE="$1"

if [ ! -f "$AAB_FILE" ]; then
    echo "❌ 错误: 文件不存在: $AAB_FILE"
    exit 1
fi

if ! command -v keytool &> /dev/null; then
    echo "❌ 错误: 未找到 keytool，请安装 Java JDK"
    exit 1
fi

TEMP_DIR=$(mktemp -d)
trap "rm -rf $TEMP_DIR" EXIT

echo "📦 正在分析: $AAB_FILE"
echo ""

unzip -q "$AAB_FILE" -d "$TEMP_DIR" 2>/dev/null || {
    echo "❌ 错误: 无法解压 AAB 文件"
    exit 1
}

CERT_FILE=$(find "$TEMP_DIR/META-INF" -name "*.RSA" -o -name "*.DSA" | head -1)

if [ -z "$CERT_FILE" ] || [ ! -f "$CERT_FILE" ]; then
    echo "❌ 错误: 未找到签名证书"
    exit 1
fi

